exports.apply = function(){
	var scope = this;
	this.formTitleIdFromHtml = function(s){
		return encodeURIComponent(s.replace(/<.*?>/g, '')
		        .replace(/&lt;/g, '<')
		        .replace(/&gt;/g, '>')
		        .replace(/&amp;/g, '&')
		        .replace(/\s/g, '-')
		        .replace(/[`@#$%^&+{}|:"!<>?=\[\]\\;,.\/"]+/g, '!')
		        .toLowerCase()).replace(/%/g, '.')
	};
	require('./page.ed').apply.call(this);
}